export const FILTER_BY_NAME = 'FILTER_BY_NAME'

export const filterByName = (input) => ({
  type: FILTER_BY_NAME,
  filterByName: { name }
  });

const INITIAL_STATE = { 
    filterByName: {name: ''},
};

function filters(state = INITIAL_STATE, action) {
  switch (action.type) {
    case FILTER_BY_NAME:
      return { ...state, filterByName: action.filterByName.name};
    default:
      return state;
  }
}

export default filters;
